mapper.select:
SELECT

FROM sys_permission

	is_deleted = 0

		AND (permission_name ILIKE CONCAT('%', #{keyword}, '%')
		  OR permission_code ILIKE CONCAT('%', #{keyword}, '%')
		  OR description ILIKE CONCAT('%', #{keyword}, '%'))


		AND status = #{status}


		AND permission_type = #{permissionType}


		AND parent_id = #{parentId}


ORDER BY sort_order ASC, id DESC
